  private def walkStringTime(txt: String) = {
    val t1  = System.currentTimeMillis()
    val ret = new StringBuilder
    var i   = 0
    val len = txt.length
    while (i < len) {
      ret.append(txt(i))
      i += 1
    }
    ret.toString()
    val t2 = System.currentTimeMillis()
    println("#######walk string time: " + (t2 - t1) + "ms")
  }

